mapper.select:
SELECT problem.id, userProblem.problemID, COUNT(*) as count, problem.name, problem.link, problem.siteID, codingSite.siteName, codingSite.siteUrl, problem.regdate FROM ((problem INNER JOIN codingSite ON problem.siteID = codingSite.id) INNER JOIN userProblem ON problem.id = userProblem.problemID) where problem.name like #{searchValue}
and problem.siteID=#{siteValue}
GROUP BY problem.id ORDER BY
problem.name problem.siteID problem.regdate desc COUNT(*) desc problem.regdate
LIMIT #{s_point}, #{list}

SELECT COUNT(*) FROM (SELECT problem.id FROM ((problem INNER JOIN codingSite ON problem.siteID = codingSite.id) INNER JOIN userProblem ON problem.id = userProblem.problemID) where problem.name like CONCAT('%',#{searchValue},'%')
and problem.siteID=#{siteValue}
GROUP BY problem.id) subquery;
